import Layout from "../../components/layout";
import { useMemo, useEffect, useState } from "react";
import { API } from "aws-amplify";
import { listContracts } from "../../../src/graphql/queries.ts";
import Link from "next/link";
import Table from "../../components/table";
import moment from "moment";

const Contracts = () => {
  const [contracts, setContracts] = useState([]);

  useEffect(() => {
    getAllContracts();
  }, []);

  const getAllContracts = async () => {
    const contracts = await API.graphql({
      query: listContracts,
    });

    setContracts(contracts.data?.listContracts.items);
  };

  const columns = useMemo(
    () => [
      {
        Header: "Contract #",
        accessor: "contractNumber",
      },
      {
        Header: "Contract Type",
        accessor: "contractType",
      },
      {
        Header: "Vendor",
        accessor: "contractTo.name",
      },
      {
        Header: "Commodity",
        accessor: "commodity.name",
      },
      {
        Header: "Begin Date",
        accessor: "beginDate",
        Cell: (value) => <span>{moment(value).format("MMM Do, YYYY")}</span>,
      },
      {
        Header: "End Date",
        accessor: "endDate",
        Cell: (value) => <span>{moment(value).format("MMM Do, YYYY")}</span>,
      },
      {
        Header: "Quantity",
        accessor: "quantity",
      },
      {
        Header: "Price",
        accessor: "price",
      },

      {
        Header: "Edit",
        accessor: "id",
        Cell: ({ value }) => (
          <Link href="/contracts/edit/[id]" as={`/contracts/edit/${value}`}>
            <a className="text-blue-600 underline hover:text-blue-800 hover:no-underline">
              {" "}
              View
            </a>
          </Link>
        ),
      },
    ],
    []
  );

  return (
    <Layout>
      <div className="px-6">
        <div className="text-center w-1/2 mx-auto py-6 text-2xl font-bold">
          <h3>Contracts</h3>
        </div>
        <div className="my-6">
          <Link href="/contracts/create">
            <a className="px-3 py-2 border border-gray-800 shadow hover:bg-gray-800 hover:text-white">
              Create New
            </a>
          </Link>
        </div>
        <div>
          <Table data={contracts} columns={columns} />
        </div>
      </div>
    </Layout>
  );
};

export default Contracts;
