import { CSVReader, readString } from "react-papaparse";
import Layout from "../../components/layout";
import { useRef, useState, useEffect } from "react";

const BulkCreateVendors = () => {
  const buttonRef = useRef();

  const handleOpenDialog = (e) => {
    // Note that the ref is set async, so it might be null at some point
    if (buttonRef.current) {
      buttonRef.current.open(e);
    }
  };

  const handleOnFileLoad = (data) => {
    console.log(data);
  };

  const handleOnError = (err, file, inputElem, reason) => {
    console.log(err);
  };

  const handleOnRemoveFile = (data) => {
    console.log(data);
  };

  const handleRemoveFile = (e) => {
    // Note that the ref is set async, so it might be null at some point
    if (buttonRef.current) {
      buttonRef.current.removeFile(e);
    }
  };

  return (
    <Layout>
      <div>
        <div>
          <h3>Bulk Upload Vendors</h3>
        </div>
        <div className="py-12 px-24">
          <CSVReader
            ref={buttonRef}
            onFileLoad={(event) => handleOnFileLoad(event)}
            onError={(err) => handleOnError(err)}
            noClick
            noDrag
            config={{ header: true }}
            onRemoveFile={(data) => handleOnRemoveFile(data)}
          >
            {({ file }) => (
              <div className="w-1/2 flex justify-items-center items-center">
                <button
                  className="w-5/12 border border-gray-900 h-ful"
                  type="button"
                  onClick={(e) => handleOpenDialog(e)}
                >
                  Browe file
                </button>
                <div
                  style={{
                    borderWidth: 1,
                    borderStyle: "solid",
                    borderColor: "#ccc",
                    height: 45,
                    lineHeight: 2.5,
                    marginTop: 5,
                    marginBottom: 5,
                    paddingLeft: 13,
                    paddingTop: 3,
                    width: "60%",
                  }}
                >
                  {file && file.name}
                </div>
                <button
                  style={{
                    borderRadius: 0,
                    marginLeft: 0,
                    marginRight: 0,
                    paddingLeft: 20,
                    paddingRight: 20,
                  }}
                  onClick={(data) => handleRemoveFile(data)}
                >
                  Remove
                </button>
              </div>
            )}
          </CSVReader>
        </div>
      </div>
    </Layout>
  );
};

export default BulkCreateVendors;
