import { Formik, Field, Form } from "formik";
import { useState, useEffect } from "react";
import Layout from "../../../components/layout";
import { API } from "aws-amplify";
import { updateContract } from "../../../../src/graphql/mutations.ts";
import {
  listVendors,
  listCommoditys,
  getContract,
} from "../../../../src/graphql/queries.ts";
import moment from "moment";
import DatePicker from "react-datepicker";
import { useRouter } from "next/router";

const EditContract = () => {
  const router = useRouter();
  const { id } = router.query;
  const [contract, setContract] = useState();
  const [commodities, setCommodities] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [dateSigned, setDateSigned] = useState(new Date());
  const [beginDate, setBeginDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  useEffect(() => {
    getMyContract();
  }, [id]);

  useEffect(() => {
    if (contract) {
      setDateSigned(new Date(contract.dateSigned));
      setBeginDate(new Date(contract.beginDate));
      setEndDate(new Date(contract.endDate));
    }
  }, [contract]);

  const getMyContract = async () => {
    const {
      data: { getContract: myContract },
    } = await API.graphql({
      query: getContract,
      variables: {
        id,
      },
    });
    setContract(myContract);
  };

  const getAllCommodities = async () => {
    const {
      data: {
        listCommoditys: { items: allCommodities },
      },
    } = await API.graphql({
      query: listCommoditys,
    });
    setCommodities(allCommodities);
  };

  const getAllVendors = async () => {
    const {
      data: {
        listVendors: { items: allVendors },
      },
    } = await API.graphql({
      query: listVendors,
    });
    setVendors(allVendors);
  };

  useEffect(() => {
    getAllCommodities();
    getAllVendors();
  }, []);

  return (
    <Layout>
      <div>
        <div className="text-center w-1/2 mx-auto py-6 text-2xl font-bold">
          <h3>Edit Contract</h3>
        </div>
        <div>
          {contract && (
            <Formik
              initialValues={{
                contractNumber: (contract && contract.contractNumber) || "",
                contractType: (contract && contract.contractType) || "",
                contractState: (contract && contract.contractState) || "",
                vendorId: (contract && contract.vendorId) || "",
                commodityId: (contract && contract.commodityId) || "",
                quantity: (contract && contract.quantity) || "",
                price: (contract && contract.price) || "",
                terms: (contract && contract.terms) || "",
                weights: (contract && contract.weights) || "",
                basis: (contract && contract.basis) || "",
                remarks: (contract && contract.remarks) || "",
                beginDate: (contract && contract.beginDate) || "",
                endDate: (contract && contract.endDate) || "",
                dateSigned: (contract && contract.dateSigned) || "",
              }}
              onSubmit={async (values, actions) => {
                await API.graphql({
                  query: updateContract,
                  variables: {
                    input: {
                      contractNumber: values.contractNumber,
                      contractType: values.contractType,
                      contractState: values.contractState,
                      vendorId: values.vendorId,
                      commodityId: values.commodityId,
                      quantity: values.quantity,
                      price: values.price,
                      terms: values.terms,
                      weights: values.weights,
                      basis: values.basis,
                      remarks: values.remarks,
                      beginDate: moment(beginDate).subtract(10, "hours"),
                      endDate: moment(endDate).add(10, "hours"),
                      dateSigned: moment(dateSigned),
                      id,
                    },
                  },
                });
                router.back();
              }}
            >
              {({ isSubmitting }) => (
                <Form>
                  <div className="w-7/12 mx-auto">
                    <div className="flex justify-between items-center mb-4">
                      <label
                        className="text-gray-900 w-1/4 md:w-1/2"
                        htmlFor="contractNumber"
                      >
                        Contract Number
                      </label>
                      <Field
                        className="form-input w-full"
                        name="contractNumber"
                        placeholder="Contract Number"
                      />
                    </div>
                    <div className="flex justify-between items-center mb-4">
                      <label
                        className="text-gray-900 w-1/4 md:w-1/2"
                        htmlFor="dateSigned"
                      >
                        Date Signed
                      </label>
                      <DatePicker
                        selected={dateSigned}
                        onChange={(date) => setDateSigned(date)}
                        className="form-input w-full"
                      />
                    </div>
                    <div className="flex justify-between items-center mb-4">
                      <label
                        className="text-gray-900 w-1/4 md:w-1/2"
                        htmlFor="beginDate"
                      >
                        Begin Date
                      </label>
                      <DatePicker
                        selected={beginDate}
                        onChange={(date) => setBeginDate(date)}
                        className="form-input w-full"
                        startDate={beginDate}
                        selectsStart
                      />
                    </div>
                    <div className="flex justify-between items-center mb-4">
                      <label
                        className="text-gray-900 w-1/4 md:w-1/2"
                        htmlFor="endDate"
                      >
                        End Date
                      </label>
                      <DatePicker
                        selected={endDate}
                        onChange={(date) => setEndDate(date)}
                        selectsEnd
                        endDate={endDate}
                        minDate={beginDate}
                        className="form-input w-full"
                      />
                    </div>
                    <div className="flex justify-between items-center mb-4">
                      <label
                        className="text-gray-900 w-1/4 md:w-1/2"
                        htmlFor="contractType"
                      >
                        Contract Type
                      </label>

                      <Field
                        className="form-select w-full"
